select count(*) from ABANDONEDANIMAL
and SPECIES = #{species}
and GENDER = #{gender}
and ANIMALSTATE = #{animalState}
and (NAME like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or AGE like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' or SNUMBER in ( select SNUMBER from SHELTER where LOCATION like '%' || #{keyword} || '%') or SNUMBER in ( select SNUMBER from SHELTER where GROUPNAME like '%' || #{keyword} || '%') )

select * from (select ROWNUM as NUM, A.* from (select * from ABANDONEDANIMAL
and SPECIES = #{species}
and GENDER = #{gender}
and ANIMALSTATE = #{animalState}
and (NAME like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or AGE like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' or SNUMBER in ( select SNUMBER from SHELTER where LOCATION like '%' || #{keyword} || '%') or SNUMBER in ( select SNUMBER from SHELTER where GROUPNAME like '%' || #{keyword} || '%') )
order by ABNUMBER desc ) A) where NUM between #{start} and #{end}
