mapper.update:
update ADOPT
set MNUMBER = #{mNumber}, ABNUMBER = #{abNumber}, RESIDENCE = #{residence}, MARITALSTATUS = #{maritalStatus}, JOB = #{job}, STATUS = #{status}
where ADNUMBER = #{adNumber}

mapper.select:
select count(*)
from ADOPT
where MNUMBER = #{mNumber}

select *
from (select ROWNUM as NUM, A.*
      from (select * from ADOPT where MNUMBER = #{mNumber} order by ADNUMBER DESC) A)
where NUM between #{start} and #{end}

select * from (select ROWNUM as NUM, AD.* from (select * from ADOPT
and ( RESIDENCE like '%' || #{keyword} || '%' or MARITALSTATUS like '%' || #{keyword} || '%' or JOB like '%' || #{keyword} || '%' or STATUS like '%' || #{keyword} || '%' or MNUMBER in ( select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') or ABNUMBER in ( select ABNUMBER from ABANDONEDANIMAL where NAME like '%' || #{keyword} || '%') )
order by ADNUMBER desc ) AD) where NUM between #{start} and #{end}

select * from (select ROWNUM as NUM, AD.* from (select * from ADOPT
and ( RESIDENCE like '%' || #{keyword} || '%' or MARITALSTATUS like '%' || #{keyword} || '%' or JOB like '%' || #{keyword} || '%' or MNUMBER in ( select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') or ABNUMBER in ( select ABNUMBER from ABANDONEDANIMAL where NAME like '%' || #{keyword} || '%') )
order by ADNUMBER desc ) AD) where (NUM between #{start} and #{end}) and STATUS = #{status}

{call declare begin
update ADOPT set STATUS = '완료' where STATUS = '처리중' and ABNUMBER = (select AD.ABNUMBER from ADOPT AD join ABANDONEDANIMAL AB on AB.ABNUMBER = AD.ABNUMBER where AD.ADNUMBER = #{pk});
update ABANDONEDANIMAL set ANIMALSTATE = '입양' where ABNUMBER in (select AD.ABNUMBER from ADOPT AD join ABANDONEDANIMAL AB on AD.ABNUMBER = AB.ABNUMBER where AD.ADNUMBER = #{pk});
end }
