mapper.update:
update DONATION
set ABNUMBER = #{abNumber}, MNUMBER = #{mNumber}, DONASUM = #{donaSum},
BANK = #{bank}, ACCOUNTHOLDER = #{accountHolder}, ACCOUNTNUMBER = #{accountNumber}
where DNUMBER = #{dNumber}

mapper.select:
select * from (select ROWNUM as NUM, D.* from (select * from DONATION
and ( BANK like '%' || UPPER(#{keyword}) || '%' or ACCOUNTHOLDER like '%' || #{keyword} || '%' or ACCOUNTNUMBER like '%' || #{keyword} || '%' or ABNUMBER in ( select ABNUMBER from ABANDONEDANIMAL where NAME like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') )
order by DNUMBER desc ) D) where NUM between #{start} and #{end}
