mapper.insert:
insert into FINDANIMALBOARD(MNUMBER, SPECIES, KIND, LOCATION, TITLE, CONTENT
,IMGPATH
)
values (#{mNumber}, #{species}, #{kind}, #{location}, #{title}, #{content}


    , #{imgPath}


    , 'no_image.png'

)

mapper.update:
update FINDANIMALBOARD
set MNUMBER = #{mNumber},
    SPECIES  = #{species},
    KIND     = #{kind},
    LOCATION = #{location},
    TITLE    = #{title},
    CONTENT  = #{content},
    WRTIME = #{wrTime}

    , IMGPATH = #{imgPath}

where FANUMBER = #{faNumber}

update FINDANIMALBOARD
set VIEWCOUNT = VIEWCOUNT + 1
where FANUMBER = #{pk}

mapper.select:
select count(*) from FINDANIMALBOARD
and SPECIES = #{species}
and AnimalState = #{animalState}
and ( TITLE like '%' || #{keyword} || '%' or CONTENT like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%')

select *
from (select ROWNUM as NUM, A.* from
(select * from FINDANIMALBOARD


        and SPECIES = #{species}



        and ANIMALSTATE = #{animalState}



        and ( TITLE like '%' || #{keyword} || '%'
        or CONTENT like '%' || #{keyword} || '%'
        or KIND like '%' || #{keyword} || '%'
        or LOCATION like '%' || #{keyword} || '%')



) A)
where NUM between #{start} and #{end}

select count(*)
from FINDANIMALBOARD
where MNUMBER = #{mNumber}

select *
from (select ROWNUM as NUM, A.*
      from (select * from FINDANIMALBOARD where MNUMBER = #{mNumber} order by FANUMBER DESC) A)
where NUM between #{start} and #{end}

select LANUMBER
from LOOKANIMALBOARD

    SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

select * from (select ROWNUM as NUM, A.* from (select * from FINDANIMALBOARD
MNUMBER = #{mNumber} and ANIMALSTATE = '매칭됨' order by FANUMBER DESC) A)
where NUM between #{start} and #{end}

select count(*)
from LOOKANIMALBOARD

    ANIMALSTATE = '매칭됨'
    and SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

select *
from (select ROWNUM as NUM, A.*
from (select * from LOOKANIMALBOARD

    ANIMALSTATE = '매칭됨'
    and SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

    ) A)

where NUM between #{start} and #{end}

mapper.update:
update FINDANIMALBOARD
set ANIMALSTATE = '매칭됨'
where FANUMBER = #{pk}

update FINDANIMALBOARD
set ANIMALSTATE = '실종'
where FANUMBER = #{pk}

update LOOKANIMALBOARD
set ANIMALSTATE = '매칭됨'
where LANUMBER = #{laNumber}

update LOOKANIMALBOARD
set ANIMALSTATE = '보호'
where LANUMBER = #{laNumber}

mapper.select:
select *
from (select ROWNUM as NUM, A.* from
    (select * from FINDANIMALBOARD
     order by FANUMBER desc
    ) A)
where NUM between #{start} and #{end}
