mapper.insert:
insert into LOOKANIMALBOARD(MNUMBER, SPECIES, KIND, LOCATION, TITLE, CONTENT
,IMGPATH
)
values (#{mNumber}, #{species}, #{kind}, #{location}, #{title}, #{content}


    , #{imgPath}


    , 'no_image.png'

)

mapper.update:
update LOOKANIMALBOARD
set MNUMBER = #{mNumber},
    SPECIES  = #{species},
    KIND     = #{kind},
    LOCATION = #{location},
    TITLE    = #{title},
    CONTENT  = #{content},
    WRTIME = #{wrTime}

    , IMGPATH = #{imgPath}

where LANUMBER = #{laNumber}

update LOOKANIMALBOARD
set VIEWCOUNT = VIEWCOUNT + 1
where LANUMBER = #{pk}

mapper.select:
select count(*) from LOOKANIMALBOARD
and SPECIES = #{species}
and ANIMALSTATE = #{animalState}
and ( TITLE like '%' || #{keyword} || '%' or CONTENT like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' )

select *
from (select ROWNUM as NUM, A.*
FROM (select *
from LOOKANIMALBOARD


        and SPECIES = #{species}



        and ANIMALSTATE = #{animalState}



        and ( TITLE like '%' || #{keyword} || '%'
        or CONTENT like '%' || #{keyword} || '%'
        or KIND like '%' || #{keyword} || '%'
        or LOCATION like '%' || #{keyword} || '%' )



) A)
where NUM between #{start} and #{end}

select count(*)
from LOOKANIMALBOARD
where MNUMBER = #{mNumber}

select *
from (select ROWNUM as NUM, A.*
      from (select * from LOOKANIMALBOARD where MNUMBER = #{mNumber} order by LANUMBER DESC) A)
where NUM between #{start} and #{end}

select FANUMBER
from FINDANIMALBOARD

    SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

select * from (select ROWNUM as NUM, A.* from (select * from LOOKANIMALBOARD
MNUMBER = #{mNumber} and ANIMALSTATE = '매칭됨' order by LANUMBER DESC) A)
where NUM between #{start} and #{end}

select count(*)
from FINDANIMALBOARD

    ANIMALSTATE = '매칭됨'
    and SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

select *
from (select ROWNUM as NUM, A.*
from (select * from FINDANIMALBOARD

    ANIMALSTATE = '매칭됨'
    and SPECIES = #{species}

        and KIND = #{kind} and LOCATION = #{location}


        and 1 = 0

    ) A)

where NUM between #{start} and #{end}

mapper.update:
update LOOKANIMALBOARD
set ANIMALSTATE = '매칭됨'
where LANUMBER = #{pk}

update LOOKANIMALBOARD
set ANIMALSTATE = '보호'
where LANUMBER = #{pk}

update FINDANIMALBOARD
set ANIMALSTATE = '매칭됨'
where FANUMBER = #{faNumber}

update FINDANIMALBOARD
set ANIMALSTATE = '실종'
where FANUMBER = #{faNumber}
