mapper.update:
update MEMBER
set PW = #{pw}, NAME = #{name}, BIRTH = #{birth}, GENDER = #{gender}, EMAIL = #{email}, PHONE = #{phone}
where MNUMBER = #{mNumber}

mapper.select:
select ID
from MEMBER
where MNUMBER = #{pk}

select NAME
from MEMBER
where MNUMBER = #{pk}

select count(ID)
from MEMBER
where ID = #{id}

select count(EMAIL)
from MEMBER
where EMAIL = #{email}

select count(PHONE)
from MEMBER
where PHONE = #{phone}

select count(*)
from MEMBER
where EMAIL = #{email}
and ID != #{id}

select count(*)
from MEMBER
where PHONE = #{phone}
and ID != #{id}

select * from (select ROWNUM as NUM, M.* from (select * from MEMBER
and ( ID like '%' || #{keyword} || '%' or NAME like '%' || #{keyword} || '%' or BIRTH like '%' || #{keyword} || '%' or EMAIL like '%' || #{keyword} || '%' or PHONE like '%' || #{keyword} || '%' or GRADE like '%' || #{keyword} || '%' or GENDER like '%' || #{keyword} || '%' )
order by MNUMBER desc ) M) where NUM between #{start} and #{end}
