mapper.update:
update MEMBER
set PW = #{pw}, NAME = #{name}, BIRTH = #{birth}, GENDER = #{gender}, EMAIL = #{email}, PHONE = #{phone}
where MNUMBER = #{mNumber}

mapper.select:
SELECT MNUMBER
from MEMBER
where ID = #{id} and PW = #{pw}

select NAME
from MEMBER
where MNUMBER = #{pk}

select BIRTH
from MEMBER
where MNUMBER = #{pk}

select PHONE
from MEMBER
where MNUMBER = #{pk}

select EMAIL
from MEMBER
where MNUMBER = #{pk}

select ID
from MEMBER
where MNUMBER = #{pk}

select count(ID)
from MEMBER
where ID = #{id}

select count(EMAIL)
from MEMBER
where EMAIL = #{email}

select count(PHONE)
from MEMBER
where PHONE = #{phone}

select count(*)
from MEMBER
where EMAIL = #{email}
and ID != #{id}

select count(*)
from MEMBER
where PHONE = #{phone}
and ID != #{id}
