mapper.update:
update SHELTER
set GROUPNAME = #{groupName}, LOCATION = #{location}, PHONE = #{phone}
where SNUMBER = #{sNumber}

mapper.select:
select *
from SHELTER S inner join ABANDONEDANIMAL AB
on S.SNUMBER = AB.SNUMBER where AB.ABNUMBER = #{abNumber}

select * from (select ROWNUM as NUM, S.* from (select * from SHELTER
and ( GROUPNAME like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' or PHONE like '%' || #{keyword} || '%' )
order by SNUMBER desc ) S) where NUM between #{start} and #{end}
