mapper.update:
update TEMPPET
set ABNUMBER = #{abNumber}, MNUMBER = #{mNumber}, TEMPDATE = #{tempDate}, TEMPPERIOD = #{tempPeriod},
    RESIDENCE = #{residence}, MARITALSTATUS = #{maritalStatus}, JOB = #{job}, STATUS = #{status}
where TNUMBER = #{tNumber}

mapper.select:
select count(*)
from TEMPPET
where MNUMBER = #{mNumber}

select *
from (select ROWNUM as NUM, T.*
      from (select * from TEMPPET where MNUMBER = #{mNumber} order by TNUMBER DESC) T)
where NUM between #{start} and #{end}

select * from (select ROWNUM as NUM, T.* from (select * from TEMPPET
and ( TEMPPERIOD like '%' || #{keyword} || '%' or RESIDENCE like '%' || #{keyword} || '%' or MARITALSTATUS like '%' || #{keyword} || '%' or JOB like '%' || #{keyword} || '%' or STATUS like '%' || #{keyword} || '%' or MNUMBER in ( select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') or ABNUMBER in ( select ABNUMBER from ABANDONEDANIMAL where NAME like '%' || #{keyword} || '%') )
order by TNUMBER desc ) T) where NUM between #{start} and #{end}

select * from (select ROWNUM as NUM, T.* from (select * from TEMPPET
and ( TEMPPERIOD like '%' || #{keyword} || '%' or RESIDENCE like '%' || #{keyword} || '%' or MARITALSTATUS like '%' || #{keyword} || '%' or JOB like '%' || #{keyword} || '%' or MNUMBER in ( select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') or MNUMBER in ( select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') or ABNUMBER in ( select ABNUMBER from ABANDONEDANIMAL where NAME like '%' || #{keyword} || '%') )
order by TNUMBER desc ) T) where (NUM between #{start} and #{end}) and STATUS = #{status}

{call declare begin
update TEMPPET set STATUS = '완료' where STATUS = '처리중' and ABNUMBER = (select T.ABNUMBER from TEMPPET T join ABANDONEDANIMAL AB on AB.ABNUMBER = T.ABNUMBER where T.TNUMBER = #{pk});
update ABANDONEDANIMAL set ANIMALSTATE = '임보' where ABNUMBER in (select T.ABNUMBER from TEMPPET T join ABANDONEDANIMAL AB on T.ABNUMBER = AB.ABNUMBER where T.TNUMBER = #{pk});
end }
