mapper.select:
select count(*)
from BOARD
where MNUMBER = #{mNumber} and KINDOFBOARD = #{kindOfBoard}

select *
from (select ROWNUM as NUM, A.*
      from (select * from BOARD where MNUMBER = #{mNumber}
            and KINDOFBOARD = #{kindOfBoard}
            order by BNUMBER DESC) A)
where NUM between #{start} and #{end}

select * from (select ROWNUM as NUM, A.* from (select * from BOARD
KINDOFBOARD = #{kindOfBoard}
and TITLE like '%' || #{keyword} || '%'
and CONTENT like '%' || #{keyword} || '%'
and ( TITLE like '%' || #{keyword} || '%' or CONTENT like '%' || #{keyword} || '%' )
and ( MNUMBER in (select MNUMBER from MEMBER where NAME like '%' || #{keyword} || '%') or MNUMBER in (select MNUMBER from MEMBER where ID like '%' || #{keyword} || '%') )
) A) where NUM between #{start} and #{end}

mapper.update:
update BOARD
set VIEWCOUNT = VIEWCOUNT + 1
where BNUMBER = #{pk}

mapper.select:
select count(*)
from BOARD B inner join BOARDREPLY BR on B.BNUMBER = BR.BNUMBER
where B.BNUMBER = #{pk}
